import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import axios from 'axios';

import Login from './components/Login';
import Register from './components/Register';
import Properties from './components/Properties';
import Favorites from './components/Favorites';
import Messages from './components/Messages';
import PropertyForm from './components/PropertyForm';

import './i18n';

axios.defaults.baseURL = 'http://localhost:3000';

const App = () => {
  const [user, setUser] = useState(null);
  const { t, i18n } = useTranslation();
  const languages = [
    { code: 'de', name: 'Deutsch' },
    { code: 'en', name: 'English' },
    { code: 'fr', name: 'Fran√ßais' },
    { code: 'es', name: 'Espa√±ol' },
    { code: 'tr', name: 'T√ºrk√ße' }
  ];

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const payload = JSON.parse(atob(token.split('.')[1]));
        setUser(payload);
        axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      } catch (e) {
        localStorage.removeItem('token');
      }
    }
  }, []);

  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem('token');
    delete axios.defaults.headers.common['Authorization'];
  };

  const changeLanguage = (lng) => {
    i18n.changeLanguage(lng);
  };

  return (
    <Router>
      <div className="min-h-screen flex flex-col">
        {/* Header */}
        <header className="bg-primary text-white p-4 shadow-md">
          <div className="container mx-auto flex justify-between items-center">
            <h1 className="text-2xl font-bold">üè° Hausgold</h1>
            <div className="flex items-center space-x-4">
              {/* Language Switcher */}
              <select
                onChange={(e) => changeLanguage(e.target.value)}
                value={i18n.language}
                className="bg-secondary text-white p-1 rounded"
              >
                {languages.map((lang) => (
                  <option key={lang.code} value={lang.code}>
                    {lang.name}
                  </option>
                ))}
              </select>

              {user ? (
                <div className="flex items-center space-x-4">
                  <span>üëã {user.email}</span>
                  <button onClick={handleLogout} className="btn-secondary text-sm">
                    {t('logout')}
                  </button>
                </div>
              ) : (
                <div className="space-x-2">
                  <Link to="/login" className="btn-secondary text-sm">
                    {t('login')}
                  </Link>
                  <Link to="/register" className="btn-primary text-sm">
                    {t('register')}
                  </Link>
                </div>
              )}
            </div>
          </div>
        </header>

        {/* Navigation */}
        {user && (
          <nav className="bg-gray-100 py-2">
            <div className="container mx-auto flex space-x-6">
              <Link to="/properties" className="text-primary hover:underline font-medium">
                {t('properties')}
              </Link>
              <Link to="/favorites" className="text-primary hover:underline font-medium">
                {t('favorites')}
              </Link>
              <Link to="/messages" className="text-primary hover:underline font-medium">
                {t('messages')}
              </Link>
              <Link to="/post" className="text-secondary hover:underline font-medium">
                ‚ûï Post Property
              </Link>
            </div>
          </nav>
        )}

        {/* Main Content */}
        <main className="flex-grow container mx-auto p-4">
          <Routes>
            <Route path="/" element={user ? <Navigate to="/properties" /> : <Navigate to="/login" />} />
            <Route path="/login" element={<Login setUser={setUser} />} />
            <Route path="/register" element={<Register />} />
            <Route path="/properties" element={user ? <Properties /> : <Navigate to="/login" />} />
            <Route path="/favorites" element={user ? <Favorites /> : <Navigate to="/login" />} />
            <Route path="/messages" element={user ? <Messages /> : <Navigate to="/login" />} />
            <Route path="/post" element={user ? <PropertyForm /> : <Navigate to="/login" />} />
          </Routes>
        </main>

        {/* Footer */}
        <footer className="bg-primary text-white text-center p-4 mt-8">
          <p>¬© 2025 Hausgold Ltd. | Jungfernstieg 24, 20354 Hamburg</p>
          <p>‚òéÔ∏è +49 (0)40 635 5427 | ‚úâÔ∏è info@hausgold.de</p>
        </footer>
      </div>
    </Router>
  );
};

export default App;
