// client/src/components/HomePage.jsx
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const HomePage = () => {
  const navigate = useNavigate();
  const [currentSlide, setCurrentSlide] = useState(0);
  const [properties, setProperties] = useState([]);

  // Fetch featured properties
  useEffect(() => {
    const fetchProperties = async () => {
      try {
        const res = await axios.get('/api/properties', {
          params: { limit: 3 }
        });
        setProperties(res.data.slice(0, 3));
      } catch (err) {
        console.error(err);
      }
    };
    fetchProperties();
  }, []);

  // Carousel logic
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % 3);
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  const slides = [
    {
      title: "Finden Sie Ihre Traumimmobilie in Deutschland",
      subtitle: "Kaufen, verkaufen oder mieten Sie Immobilien mit Vertrauen und Komfort",
      cta1: "Immobilien entdecken",
      cta2: "Immobilie anbieten",
      img: "https://images.unsplash.com/photo-1560518883-ce09059eeffa?ixlib=rb-4.0.3&auto=format&fit=crop&w=1920&q=80",
      bg: "bg-gradient-to-r from-primary/90 to-secondary/80"
    },
    {
      title: "Premium Immobilien in Top-Lagen",
      subtitle: "Exklusive Angebote in den besten Stadtteilen Deutschlands",
      cta1: "Premium Angebote",
      cta2: "Beratung anfordern",
      img: "https://images.unsplash.com/photo-1600585154340-be6161a56a0c?ixlib=rb-4.0.3&auto=format&fit=crop&w=1920&q=80",
      bg: "bg-gradient-to-r from-primary/90 to-secondary/80"
    },
    {
      title: "Traumhaftes Landleben",
      subtitle: "Entdecken Sie idyllische Immobilien abseits der Gro√üst√§dte",
      cta1: "Landimmobilien",
      cta2: "Mietobjekte",
      img: "https://images.unsplash.com/photo-1568605117036-5fe5e7bab0b7?ixlib=rb-4.0.3&auto=format&fit=crop&w=1920&q=80",
      bg: "bg-gradient-to-r from-primary/90 to-secondary/80"
    }
  ];

  const goToSlide = (index) => {
    setCurrentSlide(index);
  };

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % 3);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + 3) % 3);
  };

  return (
    <div className="flex flex-col">
      {/* Hero Carousel */}
      <section className="relative overflow-hidden h-[600px]">
        {slides.map((slide, index) => (
          <div
            key={index}
            className={`absolute inset-0 transition-opacity duration-1000 ease-in-out ${
              index === currentSlide ? 'opacity-100' : 'opacity-0'
            }`}
          >
            <div className={slide.bg}></div>
            <img
              src={slide.img}
              alt={slide.title}
              className="w-full h-full object-cover"
            />
            <div className="absolute inset-0 flex items-center justify-center">
              <div className="container mx-auto px-4 text-center text-white" data-aos="fade-up">
                <h1 className="text-4xl md:text-6xl font-bold mb-6">{slide.title}</h1>
                <p className="text-xl mb-8">{slide.subtitle}</p>
                <div className="flex flex-col sm:flex-row gap-4 justify-center">
                  <button
                    onClick={() => navigate('/properties')}
                    className="bg-white text-primary px-8 py-4 rounded-lg font-semibold hover:bg-gray-100 transition-colors"
                  >
                    {slide.cta1}
                  </button>
                  <button
                    onClick={() => navigate('/register')}
                    className="border-2 border-white text-white px-8 py-4 rounded-lg font-semibold hover:bg-white hover:text-primary transition-colors"
                  >
                    {slide.cta2}
                  </button>
                </div>
              </div>
            </div>
          </div>
        ))}

        {/* Carousel Dots */}
        <div className="absolute bottom-8 left-1/2 transform -translate-x-1/2 flex space-x-3">
          {slides.map((_, index) => (
            <button
              key={index}
              onClick={() => goToSlide(index)}
              className={`w-3 h-3 rounded-full transition-colors ${
                index === currentSlide ? 'bg-white' : 'bg-white bg-opacity-50 hover:bg-opacity-100'
              }`}
            ></button>
          ))}
        </div>

        {/* Nav Arrows */}
        <button
          onClick={prevSlide}
          className="absolute left-4 top-1/2 transform -translate-y-1/2 text-white bg-black bg-opacity-30 p-3 rounded-full hover:bg-opacity-50 transition-colors"
        >
          <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
            <polyline points="15 18 9 12 15 6"></polyline>
          </svg>
        </button>
        <button
          onClick={nextSlide}
          className="absolute right-4 top-1/2 transform -translate-y-1/2 text-white bg-black bg-opacity-30 p-3 rounded-full hover:bg-opacity-50 transition-colors"
        >
          <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
            <polyline points="9 18 15 12 9 6"></polyline>
          </svg>
        </button>
      </section>

      {/* Search Section */}
      <section className="py-12 bg-white">
        <div className="container mx-auto px-4">
          <div className="bg-white rounded-2xl shadow-xl p-6 -mt-16 mx-4">
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Standort</label>
                <input
                  type="text"
                  placeholder="Stadt, PLZ oder Adresse"
                  className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-secondary"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Preis bis</label>
                <input
                  type="number"
                  placeholder="Max. Preis"
                  className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-secondary"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Immobilientyp</label>
                <select className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-secondary">
                  <option>Alle Typen</option>
                  <option>Wohnung</option>
                  <option>Haus</option>
                  <option>Grundst√ºck</option>
                </select>
              </div>
              <div className="flex items-end">
                <button
                  onClick={() => navigate('/properties')}
                  className="w-full bg-secondary text-white p-3 rounded-lg hover:bg-opacity-90 transition-colors"
                >
                  Suchen
                </button>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Featured Properties */}
      <section className="py-16 bg-gray-50">
        <div className="container mx-auto px-4">
          <div className="text-center mb-12">
            <h2 className="text-3xl font-bold text-primary mb-4">Beliebte Immobilien</h2>
            <p className="text-gray-600">Entdecken Sie unsere aktuellen Top-Angebote</p>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {properties.length > 0 ? (
              properties.map((prop) => (
                <div
                  key={prop.id}
                  className="bg-white rounded-xl shadow-md overflow-hidden hover:shadow-xl transition-shadow cursor-pointer"
                  onClick={() => navigate(`/properties`)}
                >
                  <img
                    src={prop.image_url || "https://via.placeholder.com/640x360?text=No+Image"}
                    alt={prop.title}
                    className="w-full h-48 object-cover"
                  />
                  <div className="p-6">
                    <div className="flex justify-between items-start mb-4">
                      <span className="bg-secondary text-white px-3 py-1 rounded-full text-sm">
                        {prop.type === 'rent' ? 'Zu vermieten' : 'Zu verkaufen'}
                      </span>
                      <span className="text-2xl font-bold text-primary">
                        ‚Ç¨{prop.price.toLocaleString()}
                      </span>
                    </div>
                    <h3 className="text-xl font-semibold mb-2">{prop.title}</h3>
                    <p className="text-gray-600 mb-4">Berlin-Mitte</p>
                    <div className="flex justify-between items-center">
                      <span className="text-sm text-gray-500">üìç {prop.owner_name || 'Deutschland'}</span>
                      <button className="text-secondary hover:text-primary transition-colors">
                        <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                          <path d="M20.84 4.61a5.5 5.5 0 0 0-7.78 0L12 5.67l-1.06-1.06a5.5 5.5 0 0 0-7.78 7.78l1.06 1.06L12 21.23l7.78-7.78 1.06-1.06a5.5 5.5 0 0 0 0-7.78z"></path>
                        </svg>
                      </button>
                    </div>
                  </div>
                </div>
              ))
            ) : (
              <div className="col-span-3 text-center py-12">
                <p className="text-gray-500">Keine Immobilien gefunden. Bitte sp√§ter erneut versuchen.</p>
              </div>
            )}
          </div>

          <div className="text-center mt-12">
            <button
              onClick={() => navigate('/properties')}
              className="bg-primary text-white px-8 py-3 rounded-lg hover:bg-opacity-90 transition-colors"
            >
              Alle Immobilien anzeigen
            </button>
          </div>
        </div>
      </section>

      {/* Features Section */}
      <section className="py-16 bg-white">
        <div className="container mx-auto px-4">
          <div className="text-center mb-12">
            <h2 className="text-3xl font-bold text-primary mb-4">Warum HausGold w√§hlen?</h2>
            <p className="text-gray-600">Vertrauen Sie auf Deutschlands f√ºhrende Immobilienplattform</p>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            <div className="text-center p-6">
              <div className="w-16 h-16 bg-secondary rounded-full flex items-center justify-center mx-auto mb-4">
                <svg xmlns="http://www.w3.org/2000/svg" width="32" height="32" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="text-white">
                  <path d="M12 22s8-4 8-10V5l-8-3-8 3v7c0 6 8 10 8 10z"></path>
                </svg>
              </div>
              <h3 className="text-xl font-semibold mb-3">Sicherheit</h3>
              <p className="text-gray-600">Gepr√ºfte Immobilien und verifizierte Nutzer f√ºr maximale Sicherheit</p>
            </div>

            <div className="text-center p-6">
              <div className="w-16 h-16 bg-secondary rounded-full flex items-center justify-center mx-auto mb-4">
                <svg xmlns="http://www.w3.org/2000/svg" width="32" height="32" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="text-white">
                  <circle cx="11" cy="11" r="8"></circle>
                  <line x1="21" y1="21" x2="16.65" y2="16.65"></line>
                </svg>
              </div>
              <h3 className="text-xl font-semibold mb-3">Einfache Suche</h3>
              <p className="text-gray-600">Intuitive Filter und detaillierte Suchoptionen f√ºr perfekte Ergebnisse</p>
            </div>

            <div className="text-center p-6">
              <div className="w-16 h-16 bg-secondary rounded-full flex items-center justify-center mx-auto mb-4">
                <svg xmlns="http://www.w3.org/2000/svg" width="32" height="32" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="text-white">
                  <path d="M21 11.5a8.38 8.38 0 0 1-.9 3.8 8.5 8.5 0 0 1-7.6 4.7 8.38 8.38 0 0 1-3.8-.9L3 21l1.9-5.7a8.38 8.38 0 0 1-.9-3.8 8.5 8.5 0 0 1 4.7-7.6 8.38 8.38 0 0 1 3.8-.9h.5a8.48 8.48 0 0 1 8 8v.5z"></path>
                </svg>
              </div>
              <h3 className="text-xl font-semibold mb-3">Direkter Kontakt</h3>
              <p className="text-gray-600">Kommunizieren Sie direkt mit Eigent√ºmern und Maklern</p>
            </div>
          </div>
        </div>
      </section>

      {/* CTA Section */}
      <section className="py-20 bg-primary text-white">
        <div className="container mx-auto px-4 text-center">
          <h2 className="text-3xl font-bold mb-6">Starten Sie noch heute Ihre Immobiliensuche</h2>
          <p className="text-xl mb-8">Tausende von Immobilien warten auf Sie</p>
          <div className="flex flex-col sm:flex-row gap-4 justify-center">
            <button
              onClick={() => navigate('/register')}
              className="bg-secondary text-white px-8 py-4 rounded-lg font-semibold hover:bg-opacity-90 transition-colors"
            >
              Kostenlos registrieren
            </button>
            <button
              onClick={() => navigate('/properties')}
              className="border-2 border-white text-white px-8 py-4 rounded-lg font-semibold hover:bg-white hover:text-primary transition-colors"
            >
              Immobilien entdecken
            </button>
          </div>
        </div>
      </section>
    </div>
  );
};

export default HomePage;
